mapper.update:
UPDATE board_tb
SET views = views + 1
WHERE uid = #{uid}

mapper.select:
SELECT uid, title, text, writer, regdate, category
FROM board_tb
WHERE uid = #{uid}

mapper.update:
UPDATE board_tb
SET    title = #{title}, text = #{text}, regdate = now(), category = #{category}
WHERE uid = #{uid}

mapper.select:
SELECT COUNT(*)
FROM member_tb
WHERE id = #{id}
